import React from 'react'
import PropTypes from 'prop-types'
import Todos from '../components/Todos'

class AddNewTaskInput extends React.Component {
  constructor(props) {
    super(props);
    //this.state = {value: ""  };
    // bind so we can use "this"
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    // lift state up to Todos
    //this.setState({value : event.target.value});
    this.props.onValueChange(event.taaget.value);
  }
  handleSubmit(event){
    console.log("Submitted: " + this.state.value); // need to lift state up to parent {todos}
    this.setState({value: this.state.value});
    event.preventDefault();
  }


  render(){

    return (

          <form onSubmit={this.handleSubmit}>
            <div className='inputField form-group'>
              <input value={this.props.value} onChange={this.handleValueChange} type="text" placeholder="Your new task..." />
            </div>
            <button  type="submit" className="btn btn-secondary">Submit</button>
          </form>

          );
  }
}

export default AddNewTaskInput;
