import React from 'react'
import PropTypes from 'prop-types'

class AddNewTaskInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ""};
    // bind so we can use "this"
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    this.setState({value : event.target.value});
  }
  handleSubmit(event){
    console.log(this.state.value);
    console.log("Submitted: " + this.state.value);
    this.setState({value: this.state.value});
    event.preventDefault();
  }

  render(){
    return (
          <form onSubmit={this.handleSubmit}>
            <div className='inputField form-group'>
              <input value={this.state.value} onChange={this.handleChange} type="text" placeholder="Your new task..." />
            </div>
            <button type="submit" className="btn btn-secondary">Submit</button>
          </form>

          );
  }
}

export default AddNewTaskInput;
